import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const SkillList = () => {
  const [skills, setSkills] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchSkills();
  }, []);

  const fetchSkills = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/skills');
      
      if (!response.ok) {
        throw new Error('Failed to fetch skills');
      }
      
      const data = await response.json();
      setSkills(data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  const handleDeleteSkill = async (id) => {
    if (!window.confirm('Are you sure you want to delete this skill?')) {
      return;
    }
    
    try {
      const response = await fetch(`/api/skills/${id}`, {
        method: 'DELETE'
      });
      
      if (!response.ok) {
        throw new Error('Failed to delete skill');
      }
      
      // Refresh skills list
      fetchSkills();
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="container">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>My Robot Skills</h2>
        <Link to="/export" className="btn btn-primary">
          Create New Skill
        </Link>
      </div>
      
      {loading ? (
        <div className="text-center my-5">
          <div className="loading-spinner"></div>
          <p className="mt-3">Loading skills...</p>
        </div>
      ) : error ? (
        <div className="alert alert-danger">
          {error}
        </div>
      ) : skills.length === 0 ? (
        <div className="text-center my-5">
          <p className="lead">You haven't created any skills yet.</p>
          <Link to="/export" className="btn btn-primary mt-3">
            Create Your First Skill
          </Link>
        </div>
      ) : (
        <div className="row">
          {skills.map(skill => (
            <div className="col-md-4 mb-4" key={skill.id}>
              <div className="card skill-card h-100">
                <div className="card-header">
                  <h5 className="card-title mb-0">{skill.name}</h5>
                </div>
                <div className="card-body">
                  <p className="card-text">
                    {skill.description || 'No description provided'}
                  </p>
                  <p className="text-muted small">
                    Created: {new Date(skill.exportDate).toLocaleDateString()}
                  </p>
                </div>
                <div className="card-footer bg-transparent d-flex justify-content-between">
                  <Link to={`/skills/${skill.id}`} className="btn btn-sm btn-outline-primary">
                    View Details
                  </Link>
                  <div>
                    <a 
                      href={`/api/download/${skill.id}`} 
                      className="btn btn-sm btn-outline-success me-2"
                      download
                    >
                      Download
                    </a>
                    <button 
                      className="btn btn-sm btn-outline-danger"
                      onClick={() => handleDeleteSkill(skill.id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SkillList;
